create database books;
use books;

-- table for books data
create table bookdet(
	id INT AUTO_INCREMENT PRIMARY KEY,
    Quntity long,
    no_stars long,
    no_ratings long,
    comments text
    );
    

-- inserting basic data

select * from bookdet;
-- for updating rating
update bookdet  set no_stars=15 , no_ratings = 3 where id = 1;
-- for updating Comments
update bookdet set comments = "[\"not good\", \"good not good not good\", \"nice\", \"worst\", \"nice\"]" where id = 1;
--  for decresing qun
UPDATE bookdet 
SET quntity = quntity - 1 
WHERE id = 1;


